<?phpinclude_once('db_connectivity.php');class AccessQuery{	const DB_TABLE_USR = 'usr';	private $_db;		public function __construct()	{		$this->_bd = DbConnectivity::getInstance();	}		public function getAccess($login, $password, &$name, &$user_id)	{		$login = mysqli_real_escape_string($this->_bd->getLink(), $login);		$password = mysqli_real_escape_string($this->_bd->getLink(), $password);				$password = SHA1($password);				$query = 'SELECT id, name FROM '.self::DB_TABLE_USR." WHERE login = '$login' AND password = '$password' ";				if( !($resulSet = $this->_bd->query($query)) )			return false;				while($row = mysqli_fetch_assoc($resulSet))		{			$name = $row['name'];			$user_id = $row['id'];						return true;		}				return false;	}}?>