<?phpdate_default_timezone_set('America/La_Paz');include('../model/access_query.php');$access = new AccessQuery();$login = $_POST['tLogin'];$password = $_POST['tPassword'];$name = '';$user_id = '';if( !($access->getAccess($login, $password, $name, $user_id)) )	header('Location: ../index.php');else{	include_once('../model/query_processor.php');		session_start();		$_SESSION['usr_name'] = $name;	$_SESSION['login'] = $login;	$_SESSION['user_id'] = $user_id;	$_SESSION['shopShowDate'] = date('Y-m-d');	$_SESSION['storeShowDate'] = date('Y-m-d');			$qp = new QueryProcessor();		// Allowed stores	$alowedStores = array();	$storesArr = $qp->query_stores_array();	$storePermissionsArr = $qp->query_store_permissions_array($user_id);		foreach($storesArr as $store)	{		if( isset($storePermissionsArr[ $store['id'] ]) )		{			$aux = $storePermissionsArr[ $store['id'] ];			if($aux['mov_v'] == 'y' || $aux['mov_a'] == 'y' || $aux['mov_d'] == 'y')			{				$alowedStores[ $store['id'] ] = $store['name'];								if( !isset( $_SESSION['idStore'] ) )				{					$_SESSION['idStore'] = $store['id'];					$_SESSION['storeName'] = $store['name'];				}			}		}	}		// Allowed shops	$alowedShops = array();	$shopsArr = $qp->query_shops_array();	$shopPermissionsArr = $qp->query_shop_permissions_array($user_id);		foreach($shopsArr as $shop)	{		if( isset($shopPermissionsArr[ $shop['id'] ]) )		{			$aux = $shopPermissionsArr[ $shop['id'] ];			if($aux['mov_v'] == 'y' || $aux['mov_a'] == 'y' || $aux['mov_d'] == 'y')			{				$alowedShops[ $shop['id'] ] = $shop['name'];								if( !isset( $_SESSION['idShop'] ) )				{					$_SESSION['idShop'] = $shop['id'];					$_SESSION['shopName'] = $shop['name'];				}			}		}	}		$_SESSION['alowedStores'] = $alowedStores;	$_SESSION['alowedShops'] = $alowedShops;		header('Location: ../view/main.php');}?>